package com.trazere.util.cache;

/**
 * The <code>FiniteCacheConstants</code> class define various constants for the settings of the finite caches. DOCME
 */
public class FiniteCacheConstants {
	/** Default capacity of the finite cache. */
	public static final int DEFAULT_FINITE_CACHE_CAPACITY = 1000;

	/** Default minimun lifetime of the elements of the finite cache. */
	public static final int DEFAULT_FINITE_CACHE_MIN_LIFETIME = 60;

	/** Default maximum lifetime of the elements of the finite cache. */
	public static final int DEFAULT_FINITE_CACHE_MAX_LIFETIME = 0;

	/** Default capacity of the finite cache. */
	public static final int UNLIMITED_FINITE_CACHE_CAPACITY = 0;

	/** Default minimun lifetime of the elements of the finite cache. */
	public static final int UNLIMITED_FINITE_CACHE_MIN_LIFETIME = 0;

	/** Default maximum lifetime of the elements of the finite cache. */
	public static final int UNLIMITED_FINITE_CACHE_MAX_LIFETIME = 0;
}
